#!/usr/bin/env python
"""Apply a bad pixel map (pbm) to a DES image 
"""

# imports
from functools import partial
import ctypes
import sys
from os import path

import numpy as np

from pixcorrect import corr_util
from pixcorrect.corr_util import PrepMain, logger
from pixcorrect import proddir
from despyfits.DESImage import DESImage, DESImageCStruct

# constants

# Which section of the config file to read for this step
config_section = 'fixcol'

# exception classes
# interface functions

def fix_cols(image, bpm):
    """Fix columns marked as bad in a BPM

    :Parameters:
        - `image`: the DESImage to which the columns are to be fixed
        - `bpm`: the bad pixel mask

    Applies the correction "in place"
    """

    logger.info('Fixing columns marked by BPM')
    fix_cols_c(bpm.cstruct, image.cstruct)
    logger.debug('Finished fixing columns')

# classes
# internal functions & classes

# Lowest level access to the C library function
fixcol_lib = np.ctypeslib.load_library('libfixcol.so', path.join(proddir, 'lib')) 
fix_cols_c = fixcol_lib.fixCol
fix_cols_c.restype = ctypes.c_int
fix_cols_c.argtypes = [DESImageCStruct, DESImageCStruct]

def fix_cols_main(config):
    """A driver for fixing bad columns

    :Parameters:
        -`config`: a python ConfigParsers object

    @returns: 0 if successful
    """
    # Load files and images here if we don't want
    # bias to clean up the memory, because other drivers that
    # call bias might want it for future steps.
    in_fname = config.get(config_section, 'image_in_fname')
    im = DESImage.load(in_fname)

    bpm_fname = config.get(config_section, 'bpm')
    logger.info('reading BPM from %s' % bpm_fname)
    bpm = DESImage.load(bpm_fname)
    
    fix_cols(im, bpm)

    out_fname = config.get(config_section, 'image_out_fname')
    im.save(out_fname)

    return 0

def add_fix_cols_args(prep_main):
    prep_main.parser.add_argument('--image_in_fname', nargs=1, 
                                  default=None,
                                  help='input image file name')
    prep_main.parser.add_argument('--bpm', nargs=1, 
                                  default=None, 
                                  help='bad pixel mask filename')
    prep_main.parser.add_argument('--image_out_fname', nargs=1, 
                                  default=None,
                                  help='output image file name')
    prep_main.switch_opts[config_section]=['image_in_fname', 
                                           'bpm',
                                           'image_out_fname']

if __name__ == '__main__':
    prep_main = PrepMain(config_section, 'Fix cols marked in a bad pixel mask')
    add_fix_cols_args(prep_main)
    config = prep_main()
    try:
        fix_cols_main(config)
        sys.exit(0)
    except:
        # If we want to set specific exit status values
        # based on what exceptions get thrown, do that
        # here
        raise
