#!/usr/bin/env python
"""Do nothing to a DESImage
"""

# imports
from functools import partial
import ctypes
import sys
from os import path

import numpy as np

from pixcorrect import corr_util
from pixcorrect.corr_util import PrepMain, logger
from pixcorrect import proddir
from despyfits.DESImage import DESImage

# constants

# Which section of the config file to read for this step
config_section = 'nullop'

# exception classes
# interface functions

def nullop(image):
    """Do nothing to an image

    :Parameters:
        - `image`: the DESImage to which to do nothing

    Does nothing "in place"
    """

    logger.info('Beginning to do nothing')
    pass
    logger.debug('Done doing nothing')

# classes
# internal functions & classes

def nullop_main(config):
    """Do nothing to an image

    :Parameters:
        -`config`: a python ConfigParsers object

    @returns: 0 if successful
    """
    # Load files and images here if we don't want
    # bias to clean up the memory, because other drivers that
    # call bias might want it for future steps.
    in_fname = config.get(config_section, 'in')
    im = DESImage.load(in_fname)

    nullop(im)

    out_fname = config.get(config_section, 'out')
    im.save(out_fname)

    return 0

def add_nullop_args(prep_main):
    prep_main.parser.add_argument('-i', '--in', nargs=1, 
                                  default=None,
                                  help='input image file name')
    prep_main.parser.add_argument('-o', '--out', nargs=1, 
                                  default=None,
                                  help='output image file name')

if __name__ == '__main__':
    prep_main = PrepMain(config_section, 'Do nothing to an image')
    add_nullop_args(prep_main)
    config = prep_main()
    try:
        nullop_main(config)
        sys.exit(0)
    except:
        # If we want to set specific exit status values
        # based on what exceptions get thrown, do that
        # here
        raise
